import styled from 'styled-components';
import { colors, setFlex, fixed, demotion, below } from '../utilities';

export const Wrapper = styled.div`
    position: relative;
    height: 100vh;
    background-image: linear-gradient(${colors.blue_1}, ${colors.blue_5});    
`;

export const RentContainer = styled.div`
    position: fixed;
    width: 100%;
    margin: 9.1rem 1rem 0rem 1rem;
    ${setFlex({ x:"space-around", y:'flex-start' })};    

    ${below.md`
        background-color: red;
    `}

    .item_list{
        position: relative;
        width: 85%;    
        margin: 0rem 1rem;
        overflow: auto;         
    }
`;

export const ItemListContainer = styled.div`
    width: 100%;
    height: 70vh;    
    flex-direction: row;
    flex-wrap: wrap;
    ${demotion[2]};
    padding-top: 2rem;
    padding-left: 1rem;
    border-radius: 1.5rem;
    ${setFlex({ x:"flex-start", y:"flex-start" })};
    overflow: auto;
`;


export const ItemDetailContainer = styled.div`
    position: relative;
    width: 100%;
    height: 70vh;        
    border-radius: 1.5rem;
    overflow: auto;    
    ${demotion[2]};
`;

export const PaqContainer = styled.div`
    position: fixed;
    width: 100%;
    margin: 9.1rem 1rem 0rem 1rem;
    ${setFlex({ x:"space-around", y:'flex-start' })};    


    .paquetes_list{
        position: relative;
        width: 95%;    
        margin: 0rem 1rem;
        overflow: auto;         
    }
`;

export const PaqListContainer = styled.div`
    width: 100%;
    height: 70vh;    
    flex-direction: row;
    flex-wrap: wrap;
    ${demotion[2]};
    padding-top: 2rem;
    padding-left: 1rem;
    border-radius: 1.5rem;
    ${setFlex({ x:"flex-start", y:"flex-start" })};
    overflow: auto;
`;



