import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, below, absolute, colors} from '../../utilities';
import {SliderContainer} from '../Container';
import {SliderLink} from '../Button';

const Noryan = ({className}) => {
  return (
    <SliderContainer className={className}>
      <div className="txt_box">
        <h1 className="title">Inflables, Futbolitos, Rockolas, Maquinitas y mucho más ...!</h1>        
      </div>
      <div className="img_box">
        <img src="https://cdn1.bbcode0.com/uploads/2020/8/13/60e7cbc401775a12dda18037d6fe1e8d-full.png" alt="noryan"/>
      </div>
      <div className="link">
      <p className="txt">CDMX, Estado de México & Cuernavaca</p>
        <SliderLink>
          <Link to={'/items/inflable'} >Explorar</Link>  
        </SliderLink>
      </div>                     
    </SliderContainer>
  );
}

const SlideNoryan = styled(Noryan)`  


  .txt_box{
    text-align: center;
    margin-top: 1.5rem;
    width: 90%;

    .title{
      color: ${colors.white_2};
      font-size:3rem;
      font-weight: bolder;    
      text-shadow: 6px 6px 12px rgb(33, 63, 232, 0.9),
              -6px -6px 12px rgb(33, 63, 232, 0.8);
    } 
  }

  .link{
    margin-bottom: 2rem;

    .txt{      
      width: 100%;
      padding: 1rem 0;
      font-size:2.5rem;
      font-weight: 600;
      text-shadow: 6px 6px 12px rgb(33, 63, 232, 0.9),
              -6px -6px 12px rgb(33, 63, 232, 0.8);
    }
  }

  .logo{    
    ${absolute()};    
    width: 100%;
    height: 70vh;
    margin-top: 1rem;
    z-index: -1;
  }

  .img_box{
    width:100%;
    height: auto;    

    img{      
      height: 70vh;      
      margin-bottom: -4rem;
    }
}

  ${below.md`



  `}

  ${below.sm`    

  `}

  ${below.xsm`


  `}
`;

export default SlideNoryan;
