import React, { useState } from "react";
import styled from 'styled-components';
// import SearchItemList from '../components/items/SearchItemList';
import {setFlex, fixed, colors } from '../utilities';
import { SearchIcon } from '../elements/Icons';


function SearchBarBase({ className }) {

    const [searchKeyword, setsearchKeyword] = useState('');

    function handleSubmit (e) {
        e.preventDefault();        
    }

    function handleSearch (e) {
        searchKeyword(e.target.value);
    }

    return (
    <div className={className}>
        <form onSubmit={handleSubmit}>                             
            <input
                className="text_input"
                type="search"
                placeholder="Busca un producto..."
                name="searchKeyword"
                onChange={ e => searchKeyword(e.target.value)}
            />            
        </form>
    </div>
    );
}

const SearchBar = styled(SearchBarBase)`
    position: relative;
    width: 20%;
    height: 3rem;
    border-radius: 50%;

    .search_label{
        width: 100%;
        padding: 0.4rem;
        color: ${colors.txt_1};
        font-size: 0.9rem;
        background-color: ${colors.white_1};
        border-radius: 1rem;
    }

    .text_input{
        border: none;
        width: 14rem;
    }

    .icon_search{
        cursor: pointer;

    }
`;


export default SearchBar;






