import React, {useState, useEffect} from 'react';
import useCustomFetch from '../../hooks/useCustomFetch';
import ItemListCard from '../../layout/ItemListCard';
import ItemsFilter from '../../elements/ItemsFIlter';
import {ItemListContainer} from '../../elements/Container';

const ItemList = ({match }) => {

    const [items, setItems] = useState([]);
    const [url, setUrl] = useState(null);
    const [results, err, loading] = useCustomFetch(url);

    const category = match.params.category;

    useEffect( () => {
        setUrl(`/items/${category}`)
    }, [url, category]);

    useEffect( () => {
        setItems(results)
    }, [results]);

    const setSizeFilter = (size) => {
      setUrl(`/items/${category}/tamaño=${size}`)
    }

    return (
        <>
          <ItemListContainer>
            <h3>{category}</h3>
            <ItemsFilter category={category} setSizeFilter={setSizeFilter}/>
            {items && items.map( (item,idx) =>
                <ItemListCard
                    key={idx}
                    id={item.id}
                    nombre={item.nombre}
                    tamaño={item.tamaño}
                    precio={item.precio}
                    paquete={item.paquete}
                    category={match.params.category}
                    fotoCover={item.fotoCover}
                />
            )}            
          </ItemListContainer>
        </>
    );
};

export default ItemList;
