import React from 'react';
import styled from 'styled-components';
//import { NavLink } from 'react-router-dom';
import { colors } from '../utilities';
import Interior from '../elements/svg/Interior';
import Exterior from '../elements/svg/Exterior';
import Location from '../elements/svg/Location';
import Id from '../elements/svg/Id';


const CondicionesSeguridadBase = ({className}) => {
    return (
        <div className={className}>
            <div className="interior">
                <Interior className="security_icon" size="55"/>
                <div className="list">
                    <li className="txt-cond-interior">No se instalan juegos en casas deshabitadas.</li>
                    <li className="txt-cond-interior">Servicios en área común (Unidad Habitacional, Condominio, etc.) necesitaremos verificar que el cliente es residente.</li>
                    <li className="txt-cond-interior">En caso de que no se nos permita realizar esta verificación el servicio no podrá llevarse a cabo.</li>
                </div>
            </div>
            <div className="exterior">
                <Exterior className="security_icon" size="55"/>
                <div className="list">
                    <li className="txt-cond-exterior">Servicios en lugares públicos (parques, iglesias, oficinas, etc.) se solicitará garantizar la integridad de nuestros equipos.</li>
                    <li className="txt-cond-exterior">En caso de que no se nos proporcione lo mencionado anteriormente no se podrá dar el servicio.</li>
                </div>
            </div>
            <div className="salon">
                <Location className="security_icon" size="55"/>
                <div className="list">
                    <li className="txt-cond-salon">En lugares rentados (salones de eventos, escuelas, etc.) se solicitarán datos de contacto del responsable del lugar.</li>
                    <li className="txt-cond-salon">Solicitaremos apoyo al responsable del lugar para garantizar la seguridad de nuestros equipos.</li>
                    <li className="txt-cond-salon">En caso de que en el lugar no muestren disposición para tomar dichas medidas no se podrá dar el servicio.</li>
                </div>
            </div>
            <div className="id">
                <Id className="security_icon" size="55"/>
                <div className="list">
                    <li className="txt-cond-id">Nuestro personal se identificará tanto al momento de la instalación del servicio como al momento de retirarlo.</li>
                    <li className="txt-cond-id">Le solicitamos no permita retirar ningún equipo a quién no se identifique como empleado nuestro.</li>
                </div>
            </div>


        </div>
    );
};

const CondicionesSeguridad = styled(CondicionesSeguridadBase)`
    position: relative;
    width: 100%;
    margin-top: 8rem;


    .security_icon{
        display: inline-block;      
    }

    .list{
        display: inline-block;
        margin-left: 0.5rem;
        list-style-type: square;
    }


    .interior{
        width: 95%;
        height: 5rem;
        margin: 1rem 0 2.5rem 3rem;
    }

    .exterior{
        width: 95%;
        height: 5rem; 
        margin: 0.5rem 0 2.5rem 3rem;
    }

    .salon{
        width: 95%;
        height: 5rem;        
        margin: 0.5rem 0 2.5rem 3rem;
    }

    .id{
        width: 95%;
        height: 5rem;      
        margin: 0.5rem 0 2.5rem 3rem;
    }


    .txt-cond-interior{
        color: ${colors.white_1};
        font-size: .9rem;
        margin: 0 0 0 5rem;
        line-height: 1.1rem;
        padding-bottom: 0.7rem;
    }

    .txt-cond-exterior{
        color: ${colors.white_1};
        font-size: .9rem;
        margin: 0 0 0 5rem;
        line-height: 1.1rem;
        padding-bottom: 0.7rem;
    }

    .txt-cond-salon{
        color: ${colors.white_1};
        font-size: .9rem;
        margin: 0 0 0 5rem;
        line-height: 1.1rem;
        padding-bottom: 0.7rem;        
    }

    .txt-cond-id{
        color: ${colors.white_1};
        font-size: .9rem;
        margin: 0 0 0 5rem;
        line-height: 1.1rem;
        padding-bottom: 0.7rem;
    }


`



export default CondicionesSeguridad;
