import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, below, absolute, colors} from '../../utilities';
import {SliderContainer} from '../Container';
import {SliderLink} from '../Button';

const Noryan = ({className}) => {
  return (
    <SliderContainer className={className}>
      <div className="txt_box">
        <h1 className="title">Inflables, Futbolitos, Rockolas, Maquinitas y mucho más ...!</h1>        
      </div>
      <div className="link">
      <p className="txt">CDMX, Estado de México & Cuernavaca</p>
        <SliderLink>
          <Link to={'/items/inflable'} >Explorar</Link>  
        </SliderLink>
      </div>               
      <div className="logo"></div> 
    </SliderContainer>
  );
}

const SlideNoryan = styled(Noryan)`  
  ${setFlex({x:"space-between"})};

  .txt_box{
    text-align: center;
    margin-top: 1.5rem;
    width: 90%;

    .title{
      color: ${colors.white_2};
      font-size:3rem;
      font-weight: bolder;    
      text-shadow: 6px 6px 12px rgb(33, 63, 232, 0.9),
              -6px -6px 12px rgb(33, 63, 232, 0.8);
    } 
  }

  .link{     
    margin-bottom: 2rem;

    .txt{      
      width: 100%;
      padding: 1rem 0;
      font-size:2.5rem;
      font-weight: 600;
      text-shadow: 6px 6px 12px rgb(33, 63, 232, 0.9),
              -6px -6px 12px rgb(33, 63, 232, 0.8);
    }
  }

  .logo{    
    ${absolute()};
    background-image: url('https://cdn1.bbcode0.com/uploads/2020/8/13/60e7cbc401775a12dda18037d6fe1e8d-full.png');
    background-position: center;
    background-repeat: no-repeat;
    width: 100%;
    height: 70vh;
    margin-top: 1rem;
    z-index: -1;
  }

  ${below.md`

    .txt_box{      
      margin-top: 2rem;
      width: 100%;

    .title{      
      font-size:2.5rem;      
    } 
  }

    .link{     
      margin-bottom: 2rem;

      .txt{              
        font-size:2rem;        
      }
    }

    .logo{
      width: 100%;
      height: 50vh;
      margin-top: 1rem;
      z-index: -1;
    }

  `}

  ${below.sm`    

  `}

${below.xsm`

    .title{      
      width: 90%;
      ${setFlex({y:"flex-start"})};
      font-size: 1.4rem;
      margin-top: 0rem;
    }

    .logo{      
      background-image: url('https://i.postimg.cc/tR2VhhzY/turtle-sm.png');
      width: 100%;
      transform: scale(1);
      display: block;
      margin-top: 2rem;
    }

    .txt{
      width: 90%;
      font-size: 1.2rem;
      font-weight: 600;
      letter-spacing: .1rem;
      height: 2rem;
      margin-top: 0rem;
    }
  `}
`;

export default SlideNoryan;
