import React from 'react'
import {Link} from 'react-router-dom';
import styled from 'styled-components';
import {colors, setFlex, below, absolute} from '../utilities';
import Back from '../elements/svg/Back';

function Card({className, nombre, desc, observaciones, precio, medidas, fotoCover, fotos, back}) {
    return (                		    
      <div className={className} >        
          <div className="icon"><Back/></div>
          <h2 className="title">{nombre}</h2>
        <div className="container">
          {/* ---  DESCRIPTION --- */}
          <div className="dtl__box">         
            <div className="description">
              <h4>Descripción</h4>
              <div className="desc_txt">{desc}</div>
            </div>
            <h4>Medidas</h4>                   
              <div className="medidas">
              <span className="ms">Ancho: <p>{medidas && medidas[0]}m</p> </span>
              <span className="ms">Largo: <p>{medidas && medidas[1]}m</p> </span>
              <span className="ms">Alto: <p>{medidas && medidas[2]}m</p> </span>
              <div className="precio">
                <p>Costo por día: <span className="price">${precio}</span></p>
              </div>
              </div>                                                       
            <p className="observaciones">{observaciones}</p>
          </div>    
          <Link to='/contacto' className="round-black-btn">Reservar</Link>

          {/* ---  PICTURES --- */}
          <div className="product-pics">
            <div className="foto_cover">
              <img src={fotoCover} alt={nombre} className="card__img"/>
            </div>
            <div className="fotos">
            {fotos && fotos.map( (foto, idx) => 
              <img  key={idx}  src={foto}  alt="" className="card__img"/>
            )}
            </div>
          </div>
        </div>
      </div>        


    )
}

const ItemDetailCard = styled(Card)`
  width: 100%;
  margin: 0rem auto;
  padding: 1rem 1.5rem;
  color: ${colors.white_1};
  position: relative;

  .icon{
    ${absolute({ x:"1.5rem", y:"1rem" })};
    cursor: pointer;
  }

  h2{
    width: 100%;
    text-align:center;
    text-transform: uppercase;
    font-size: 1.5rem;
  }

  .container{
    ${setFlex({ x:"space-between", y:'flex-start' })};

    .dtl__box{        
      width: 50%;
      font-size: .8rem;    

    h4{
      font-size: 1.2rem;    
      margin: 1rem 0rem;      
    }  

    .desc_txt{
      line-height: 1.5rem;
      text-align: justify;
      margin-bottom: 3rem;
      font-size: 1rem;
      font-weight: 500;
    }      

    .medidas{
      ${setFlex({ x:"flex-start"})};
      width: 100%;
      line-height: 1.5rem;      
      padding: 0rem 1rem;
      border-radius: .8rem;

      .ms{        
        width: 33%;
        font-weight: 500;
        font-size: 1rem;

        p{
          padding: 0rem;
          margin: 0rem; 
          font-weight: normal;          
        }
      }  
    }      

    .precio{
      width: 100%;
      font-size: 1rem;      
      text-align: right;

      p {                    
        letter-spacing: .1rem;
        .price{
          font-weight: bolder;          
        }
      }
    }   

    .observaciones{      
      line-height: 1.1rem;
      margin-top: 2rem; 
      font-weight: 500;   
    }
  } /* finish dtl_box */

    .product-pics{    
      background-color: orange;   
      width: 50%;
      height: 35vh;    

      img{
        height: 4rem;
      }
    } /* finish product-pics */
  }


  ${below.md`    

    .container{
      flex-direction: column-reverse;    

      .dtl__box{
        width: 100%;     
      }
      .product-pics{
        width: 100%      
      }
    }
  `}
`;

export default ItemDetailCard
