import React from 'react';
import styled from 'styled-components';
import {setFlex, colors, elevation, below} from '../utilities';
import { NavLink } from 'react-router-dom';
import Rent from '../elements/svg/Rent';
import Security from '../elements/svg/Security';
import Payment from '../elements/svg/Payment';
import Cancellations from '../elements/svg/Cancellations';

const ConditionsMenuBase = ({ className }) => {

    return (

        <div className={className}>
            <div className="table">
                <ul className="nav_list">
                    <li className="nav_condition">
                        <NavLink 
                            className="nav_link" 
                            activeClassName="active_link"             
                            exact to={`/condiciones`}>          
                            <Rent className="icon" size={35}/>
                            <p className="text">Renta</p>
                        </NavLink>
                    </li>
                    <li className="nav_condition">
                        <NavLink
                            className="nav_link" 
                            activeClassName="active_link"             
                            exact to={`/condiciones/seguridad`}>        
                            <Security className="icon" size={35}/>
                            <p className="text">Seguridad</p>
                        </NavLink>
                    </li>
                    <li className="nav_condition">
                        <NavLink
                            className="nav_link" 
                            activeClassName="active_link"             
                            exact to={`/condiciones/pago`}>        
                            <Payment className="icon" size={35}/>
                            <p className="text">Pago</p>
                        </NavLink>
                    </li>
                    <li className="nav_condition">
                        <NavLink
                            className="nav_link" 
                            activeClassName="active_link"             
                            exact to={`/condiciones/cancelaciones`}>        
                            <Cancellations className="icon" size={35}/>
                            <p className="text">Cancelaciones</p>
                        </NavLink>
                    </li>
                </ul>
            </div>
        </div>
    )
}

const ConditionsMenu = styled(ConditionsMenuBase)`
    width: 18vw;
    display: inline-block;
    margin: 0 1rem 0 2.5rem;

    .table{
        margin-top: 0rem;
    }

    li{
        margin-top: 1.5rem;
    }

    .icon{
        display: inline-block;
    }

    .text{
        display: inline-block;
        margin-left: 1rem;
        font-size: 1.1rem;
        letter-spacing: .1rem;
        font-weight: 700;
        padding-bottom: 0.7rem;
    }


    .nav_list{
        position: relative;
        list-style: none;
        width: 100%;

        .nav_condition{
          height: 5.5rem;        
          display: block;        
          border-top-left-radius: .5rem;
          border-top-right-radius: .5rem;
          ${elevation[5]};
          border-bottom-left-radius: .5rem;
          border-bottom-right-radius: .5rem;
          ${setFlex({ x:"flex-start"})};

          &:hover{
              background-color: ${colors.white_trans};
              ${elevation[2]};
          }

          .nav_link{
            width: 100%;
            margin-top: 1.5rem;
            margin-left: 1rem;
            color: ${colors.white_1};
            text-decoration: none;
            text-transform: capitalize;
            font-size: 1rem;
            font-weight: bold;
            cursor: pointer;
          }
        }
    }

    .active_link{
        height: 5.5rem;        
        display: block;
        cursor: pointer;
        border-top-left-radius: .5rem;
        border-top-right-radius: .5rem;
        border-bottom-left-radius: .5rem;
        border-bottom-right-radius: .5rem;
        background-color: ${colors.blue_2};
        ${elevation[7]};
    }

    ${below.lg`

        display: inline;
        margin: 0 1rem 0 0rem;

        .table {
            width: 90%;
            display: table;   /* Allow the centering to work */
            margin: 0 auto;
        }

        li{
            display: inline;
            float: left;
            margin: 1rem 1rem 2rem 1rem;
            width: 20%;
        }

        .text{
            margin-left: 0.5rem;
            font-size: 1rem;
            letter-spacing: 0rem;
            font-weight: 600;
            padding-bottom: 0.5rem;
        }

    `};

    ${below.md`

        li{
            display: flex;
            float: center;
            margin: 1rem 3rem 1rem 3rem;
            width: 30%;
            flex-wrap: wrap;
        }

        .text{
            margin-left: 0.3rem;
            font-size: 0.7rem;
            letter-spacing: 0rem;
            font-weight: 400;
            padding-bottom: 0.4rem;
        }




    `};

    ${below.sm`


        li{
            display: flex;
            float: center;
            margin: 1rem 0.5rem 1rem 0rem;
            width: 40%;
            flex-wrap: wrap;
            flex-direction: row;
        }

        .text{
            margin-left: 0.3rem;
            font-size: 0.8rem;
            letter-spacing: 0rem;
            font-weight: 300;
            padding-bottom: 0.3rem;
        }




    `};

`;

export default ConditionsMenu;
