import React, {useState, useEffect} from 'react';
import useCustomFetch from '../../hooks/useCustomFetch';
import {PaqDetailContainer} from '../../elements/Container';
import ItemDetailCard from '../../layout/ItemDetailCard';

function PaqDetail({match}) {

    const [paquetesResponse, setPaquetesResponse] = useState([]);
    const [url, setUrl] = useState(null);
    const [results, result, err, loading, paquete] = useCustomFetch(url);

    useEffect( () => {
        setUrl(`/paquetes/${match.params.id}`)
    }, [url, match.params.id]);

    useEffect( () => {
        setPaquetesResponse(paquete)
    }, [paquete]);

    return (
        <PaqDetailContainer>
            {loading && url  && <div>LOADING</div>}
            <ItemDetailCard
                nombre={paquete.nombre}
                precio={paquete.precioPaquete}
                desc={paquete.descripcion}
                observaciones={paquete.observaciones}
                medidas={paquete.medidas}
            />            
        </PaqDetailContainer>
    )
}

export default PaqDetail;
