import React, {useState} from 'react';
import styled from 'styled-components';
import {absolute} from '../utilities';

const SliderBase = ({className}) => {

  const [x, setX] = useState(0);

  let sliderComponents = [1,2,3,4]; 

  const goLeft = () => {
    setX(x + 100);
    console.log('click left')
    console.log(x)
  }

  const goRight = () => {
    setX(x - 100);
    console.log('click right')
    console.log(x)
  }

  return (

    <div className={className}>
      {sliderComponents.map( (component,idx) => {
            return (
              <div key={idx} className="slide" style={{ transform:`translateX(${x})` }}>
                {component}
              </div>
            )
      })}

      <button className="slide_btn_left" onClick={goLeft}>left</button>
      <button className="slide_btn_right" onClick={goRight}>right</button>
    </div>
  );
}

const Slider = styled(SliderBase)`
  background-color: red;
  width: 100%;
  height: 90vh;
  margin: 0;
  padding: 0;
  display: flex;
  align-items: center;  

  .slide{ 
    border: solid 3px blue;
    min-width: 100%;
    height: 80%;
    position: relative;
    transition: .5s;
  }

  .slide_btn_left{
    background-color: yellowgreen;
    ${absolute({ y:"55%" })};
    transform: translateY(-50%);
    width: 5%;
    height: 70%;
    cursor: pointer;
  }

  .slide_btn_right{
    background-color: yellowgreen;
    ${absolute({ y:"55%", xP:"right", x:".5rem" })};
    transform: translateY(-50%);
    width: 5%;
    height: 70%;
    cursor: pointer;
  }

`;


export default Slider;
