import React from 'react';
import styled from 'styled-components';
import {setFlex, elevation, colors } from '../../utilities';

const Fut = ({className}) => {
  return (
    <div className={className}>
      <h3>Futbolitos</h3>
    </div>
  );
}

const SlideFut = styled(Fut)`
  background-image: linear-gradient(to right, ${colors.blue_2}, #AB05F2);
  ${setFlex()};
  color: white;
  width: 98%;
  height: 65vh;
  font-size: 4rem;
  margin: 1rem auto;
  border-radius: .7rem;
  ${elevation[5]}
`;

export default SlideFut;
