import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {colors, below, absolute, setFlex} from '../../utilities';
import {SliderContainer} from '../Container';
import Joy from '../svg/Joy.jsx';
import {SliderLink} from '../Button';

const Maquinitas = ({className}) => {
  return (
    <SliderContainer className={className}> 
      <div className="icon"><Joy size={40}/></div>      
      <h3 className="title slide_title">Maquinitas</h3>


      <div className="img_box">
        <img className="img" src="https://res.cloudinary.com/noryan/image/upload/v1596031579/foto_covers/maquinita_xbox.png" alt=""/>      
      </div>

      <div className="text_box">
        <p className="txt">Xbox y Arcade</p>
        <p className="txt">Disfruta de m√°s de #titulos diferentes</p>
      </div> 

      <div className="link">
        <SliderLink>
        <Link to={'/items/maquinita'}>Explorar</Link> 
        </SliderLink>
      </div>

    </SliderContainer>
  );
}

const SlideMaquinitas = styled(Maquinitas)`
  background-image: linear-gradient(to right, ${colors.blue_2}, ${colors.blue_6} );  
  position: relative;

  .title{
    width: auto;
    margin: 1rem auto;
    padding-top: 1.5rem;
    padding-left: 10%;    
  }

  .img_box{
    height: 70%;
    img{

    }
  }



`;

export default SlideMaquinitas;
