import React from 'react';
import styled from 'styled-components';
import {setFlex, elevation, colors} from '../../utilities';

const Rockolas = ({className}) => {
  return (
    <div className={className}>
      <h3>Rockolas y Karaoke</h3>
    </div>
  );
}

const SlideRock = styled(Rockolas)`
  background-image: linear-gradient(to right, ${colors.noryan_blue}, ${colors.noryan_prpl} );
  ${setFlex()};
  color: white;
  width: 98%;
  height: 65vh;
  font-size: 4rem;
  margin: 1rem auto;
  border-radius: .7rem;
  ${elevation[5]};  
`;

export default SlideRock;
