import React from 'react';
import styled from 'styled-components';
import {setFlex, fixed, colors, demotion, elevation, absolute} from '../utilities';
import { NavLink } from 'react-router-dom';
import Bill from '../elements/svg/Bill';
import Functioning from '../elements/svg/Functioning';
import Register from '../elements/svg/Register';
import Download from '../elements/svg/Download';

const BusinessMenuBase = ({ className }) => {

    return (

        <div className={className}>
            <ul className="nav_list">
                <li className="nav_business">
                    <NavLink 
                        className="nav_link" 
                        activeClassName="active_link"             
                        exact to={`/negocio`}>          
                        <Bill className="icon" size={45}/>
                        <p className="text">Ventajas</p>
                    </NavLink>
                </li>
                <li className="nav_business">
                    <NavLink
                        className="nav_link" 
                        activeClassName="active_link"             
                        exact to={`/negocio/funcionamiento`}>        
                        <Functioning className="icon" size={35}/>
                        <p className="text">Funcionamiento</p>
                    </NavLink>
                </li>
                <li className="nav_business">
                    <NavLink
                        className="nav_link" 
                        activeClassName="active_link"             
                        exact to={`/negocio/registro`}>        
                        <Register className="icon" size={35}/>
                        <p className="text">Registro</p>
                    </NavLink>
                </li>
                <li className="nav_business">
                    <NavLink
                        className="nav_link" 
                        activeClassName="active_link"             
                        exact to={`/negocio/catalogo`}>        
                        <Download className="icon" size={35}/>
                        <p className="text">Catálogo</p>
                    </NavLink>
                </li>
            </ul>
        </div>
    )
}

const BusinessMenu = styled(BusinessMenuBase)`
    width: 19vw;
    margin-top: 6rem;
    position: relative;

    li{
        margin-top: 2rem;
    }

    .icon{
        display: inline-block;
    }

    .text{
        display: inline-block;
        margin-left: 1rem;
        font-size: 1.1rem;
        letter-spacing: .1rem;
        font-weight: 700;
        padding-bottom: 0.7rem;
    }


    .nav_list{
        position: relative;
        list-style: none;
        // margin-left: 2rem;
        // padding: 3rem 0;
        // display: block;   
        width: 100%;

        .nav_business{
          position: relative;        
          height: 5.5rem;        
          display: block;        
          border-top-left-radius: .5rem;
          border-top-right-radius: .5rem;
          ${elevation[5]};
          border-bottom-left-radius: .5rem;
          border-bottom-right-radius: .5rem;
          ${setFlex({ x:"flex-start"})};

          &:hover{
              background-color: ${colors.white_trans};
              ${elevation[2]};
          }

          .nav_link{
            width: 100%;
            // padding: 1rem;
            margin-top: 1.5rem;
            margin-left: 1rem;
            color: ${colors.white_1};
            text-decoration: none;
            text-transform: capitalize;
            font-size: 1rem;
            font-weight: bold;
            cursor: pointer;
          }
        }
      }

      .active_link{
        position: relative;        
        height: 5.5rem;        
        display: block;
        cursor: pointer;
        border-top-left-radius: .5rem;
        border-top-right-radius: .5rem;
        border-bottom-left-radius: .5rem;
        border-bottom-right-radius: .5rem;
        background-color: ${colors.blue_2};
        ${elevation[7]};
      }

`;

export default BusinessMenu;
