import React from 'react';
import { NavLink } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, colors, below, fixed} from '../utilities';

function NavBarBase({ className, open }) {
    return (
      <ul className={className}>
          <li className="nav_item"> <NavLink activeClassName="active_link" className="nav_link" exact to="/items">Catálogo de renta</NavLink></li>
          <li className="nav_item"> <NavLink activeClassName="active_link" className="nav_link" exact to="/paquetes">Renta de paquetes</NavLink></li>
          <li className="nav_item"> <NavLink activeClassName="active_link" className="nav_link" exact to="/venta">Catálogo de venta</NavLink></li>
          <li className="nav_item"> <NavLink activeClassName="active_link" className="nav_link" exact to="/negocio">Inicia tu negocio</NavLink></li>
          <li className="nav_item"> <NavLink activeClassName="active_link" className="nav_link" exact to="/condiciones">Condiciones</NavLink></li>
          <li className="nav_item"> <NavLink activeClassName="active_link" className="nav_link" exact to="/nosotros">Nosotros</NavLink></li>
          <li className="nav_item"> <NavLink activeClassName="active_link" className="nav_link" exact to="/contacto">Contacto</NavLink></li>
      </ul>
    )
}

const NavbarList = styled(NavBarBase)` 
    position: relative;
    width: 100%;
    background-color: ${colors.blue_1};
    ${setFlex({x:"space-around"})};
    list-style: none;
      margin: 1rem auto;
      display: flex;
      flex-flow: row nowrap;

      .nav_item { 

      }  
      .nav_link{ 
        font-size: 1rem;
        color: ${colors.white_1};
        text-decoration: none;
        display: block;
        margin: 0 1.5rem;
      }

    ${below.md`             

        .nav_list{
            flex-flow: column nowrap;            
            height: 100vh; 
            width: 100%;
            ${fixed({x:"-1rem", y:"3.5rem" })}

            transform: ${({ open }) => open ? 'translateX(0)' : 'translateX(100%)'};
            transition: transform 0.3s ease-in-out;  
        }

        .nav_item{
            margin 1.4rem auto;
        }

        .nav_link{ 
            font-size: 1rem;
            color: ${colors.white_1};
            text-decoration: none;
            display: block;          
        } 
    `};


    /* Active Style */
    .nav_list .active_link{ 
        font-weight: bold;
        font-size: 1.1rem;
        color: ${colors.blue_3};
    }

`;

export default NavbarList;
