      }      

    } /* finish product-pics */
  }

  .book_btn{
    background-color: teal;
    width: 50%;

  }


  ${below.md`
    padding: .2rem 1rem;

    .icon{
      ${absolute({ x:"1rem", y:".5rem" })};      
    }

    h2{
      margin-top: 2rem;      
    }

    .container{
      flex-direction: column-reverse;    

      .dtl__box{
        width: 100%;

        h4{
          text-align:center;
          margin: 2rem auto;
        }

        .precio{
          text-align: center;
          margin-bottom: 1rem;

          p {            
