import React, {useState} from 'react';
import styled from 'styled-components';
import {setFlex, colors, below} from '../utilities';

function Filter({className}) {

  const [checked, setChecked] = useState('');

  function handleChange(e){
    if(e.target.checked){ 
      setChecked(e.target.value);      
    }
  }

  const nextAddressStep = (e) => {   
    e.preventDefault();    
    console.log('sameAddress question response saved ...')
    if(checked === 'NO'){

    }
    if(checked === 'YES'){

    }        
  }

  return (
    <div className={className}>      
      <form onSubmit={nextAddressStep}>      
          <div className="radio">
            <div className="r_1">
                <input                   
                  type="radio"                                    
                  value="Chicos"                  
                  checked={checked === "Chicos"}
                  onChange={handleChange}/>
                <label htmlFor="radio-1" className="radio-label"><span>Chicos</span></label>
            </div>
            <div className="r_2">
                <input                   
                  type="radio"                                                      
                  value="Medianos"
                  checked={checked === "Medianos"}
                  onChange={handleChange}/>                  
                <label  htmlFor="radio-2" className="radio-label"><span>Medianos</span></label>
            </div>
            <div className="r_3">
                <input                   
                  type="radio"                                                      
                  value="Grandes"
                  checked={checked === "Grandes"}
                  onChange={handleChange}/>                  
                <label  htmlFor="radio-2" className="radio-label"><span>Grandes</span></label>
            </div>
            <div className="r_4">
                <input                   
                  type="radio"                                                      
                  value="Precio"
                  checked={checked === "Precio"}
                  onChange={handleChange}/>                  
                <label  htmlFor="radio-2" className="radio-label"><span>Precio</span></label>
            </div>
          </div>          
      </form>
    </div>
  );
}

const ItemFilter = styled(Filter)`
    position: relative;
    width: 100%;
    margin: 0 auto;
    background-color: red;     

    span{
      font-size: 1rem;
    }

    .radio{
      width: 100%;
      margin: 2rem 0;    
      ${setFlex({ x:"space-between"})};    
    }

    .radio input[type="radio"] {
      position: absolute;
      opacity: 0;
      z-index: 999;
      width: 6rem;
      height: 2rem;      
    }

    .radio input[type="radio"] + .radio-label::before {
      content: '';
      background: ${colors.noryan_blue};
      border-radius: 3rem;
      border: none;
      display: inline-block;
      width: 6.5em;
      height: 3em;
      top: .3rem;
      position: relative;      
      margin-right: 2.5em;
      vertical-align: top;
      cursor: pointer;
      text-align: center;      
      z-index: 1; 
    }

    .radio input[type="radio"]:checked + .radio-label::before {
      background-color: ${colors.blue_6};
      z-index: 2;
    }

    .radio input[type="radio"] + .radio-label:empty::before {
      margin-right: 0;
      z-index: 3;
    }

    .radio-label{
      position: relative;

      span{      
        color: ${colors.white_1};
        position: absolute;
        top: 100%;
        left: 30%;
        z-index: 4;
        font-size: .8rem;
      }
    }


    .button{
      margin: 2.5rem 0; 
      ${setFlex()};
    }

    ${below.md`                      
       .address_txt {
        font-size: .8rem;       
      }
  `}
`;

export default ItemFilter;
