import {ITEM_LIST_REUQEST, ITEM_LISTS_SUCESS, ITEM_LIST_FAIL} from '../actions/types'

function itemReducer(state={ items: []}, action){

  switch(action.type){
    case ITEM_LIST_REUQEST:
      return { loading: true}
    case ITEM_LISTS_SUCESS:
      return { loading: false, items: action.payload} 
    case ITEM_LIST_FAIL:
      return {loading: false, error: action.payload }
    default:
      return state;  
  }
}

export {itemReducer};
