import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, below, colors, demotion, elevation} from '../../utilities';
import {SliderContainer} from '../Container';
import {SliderLink} from '../Button';

const Noryan = ({className}) => {
  return (
    <SliderContainer className={className}>
      <h1 className="title">Inflables, Futbolitos, Rockolas, Maquinitas y mucho más ...!</h1>
      <div className="logo"></div>
      <SliderLink>
      <Link to={'/items/inflable'} >Explorar</Link>  
      </SliderLink>
      <p className="txt">CDMX, Estado de México & Cuernavaca</p>
    </SliderContainer>
  );
}

const SlideNoryan = styled(Noryan)`
  background-image: linear-gradient(to left, ${colors.blue_5}, ${colors.blue_1} );
  ${setFlex()};  
  text-align: center;

  .logo{
    position: relative;
    background-image: url('https://cdn1.bbcode0.com/uploads/2020/7/13/fcae2fb3af62db254607c2671ed3176b-full.png');
    background-position: center;
    background-repeat: no-repeat;
    width: 30%;
    height: 40%;
    margin-top: .5rem;
    transform: scale(1.3);
  }

  ${below.md`
    .title{

      ${setFlex({y:"flex-start"})};
      font-size: 2.3rem;
    }
    .logo{
      width: 50%;
      transform: scale(1);
    }
  `}

  ${below.sm`
    .title{      
      width: 90%;
      ${setFlex({y:"flex-start"})};
      font-size: 1.8rem;
      line-height: 2rem;
      margin-top: 2rem;
    }
    .logo{
      width: 100%;
      transform: scale(.8);
    }
  `}
`;

export default SlideNoryan;
