import React, {useState, useEffect} from 'react';
import styled from 'styled-components';
import useCustomFetch from '../../hooks/useCustomFetch';
import PaqListCard from '../../layout/PaqListCard';
import {ItemListContainer} from '../../elements/Container';
import {colors} from '../../utilities'

const PaqList = ({match }) => {

    const [items, setItems] = useState([]);
    const [url, setUrl] = useState(null);
    const [results, err, loading] = useCustomFetch(url);

    useEffect( () => {
        setUrl(`/paquetes`)
    }, [url, match.params.paquete]);

    useEffect( () => {
        setItems(results)
    }, [results]);

    return (
        <>          
          <ItemListContainer>
            {items.map( (item,idx) =>              
                <PaqListCard                     
                    key={idx}
                    id={item.id}
                    nombre={item.nombre}
                    tamaño={item.tamaño}
                    precio={item.precio}                    
                    paquete={item.paquete}
                    // category={match.params.category} 
                />
            )}            
          </ItemListContainer>
        </>
    );
};

export default PaqList;
