import React, {useState, useEffect} from 'react';
import useCustomFetch from '../../hooks/useCustomFetch';
import ItemListCard from '../../layout/ItemListCard';
import {ItemListContainer} from '../../elements/Container';

const ItemList = ({match }) => {

    const [items, setItems] = useState([]);
    const [url, setUrl] = useState(null);
    const [results] = useCustomFetch(url);

    useEffect( () => {
        setUrl(`/items/${match.params.category}`)
    }, [url, match.params.category]);

    useEffect( () => {
        setItems(results)
    }, [results]);

    return (
        <>          
          <ItemListContainer>
            {items.map( (item,idx) =>              
                <ItemListCard                     
                    key={idx}
                    id={item.id}
                    nombre={item.nombre}
                    tamaño={item.tamaño}
                    precio={item.precio}                    
                    paquete={item.paquete}
                    category={match.params.category} 
                />
            )}            
          </ItemListContainer>
        </>
    );
};

export default ItemList;
