import React from 'react';
import styled from 'styled-components';
import {setFlex, fixed, colors, elevation, absolute} from '../../utilities';
import Many from '../../elements/svg/Many';
import Event from '../../elements/svg/Event';
import PriceEvent from '../../elements/svg/PriceEvent';


const NosotrosBase = ({className}) => {
    return (
        <div className={className}>
            <h1 className="nosotros">Acerca de Noryan</h1>
            <p className="txt_nosotros">Ofrecemos juegos novedosos, limpios, en excelentes condiciones, diseñados para la diversión y seguridad de los niños. La atención que ofrecemos es amable, respetuosa, puntual y preocupada por satisfacer sus necesidades.</p>
            <p className="txt_dos_nosotros">Nuestros precios son por evento diario (no por un número determinado de horas) y contamos con diferentes formas de pago.</p>
            <div className="modelos_dif">
                <Many className="many_icon" size="50"/>
                <h2 className="title_modelos_dif">Modelos Diferentes</h2>
                <p className="txt_mod_diferentes">Originales y unicos en México garantizan que tenemos el juego perfecto para tu evento.</p>
            </div>
            <div className="eventos">
                <Event className="event_icon" size="50"/>
                <h2 className="title_eventos">58,000 eventos</h2>
                <p className="txt_eventos">Durante más de 17 años con una gran tasa de satisfacción nos respaldan.</p>
            </div>
            <div className="precio">
                <PriceEvent className="precio_icon" size="50"/>
                <h2 className="title_precio">Precio por evento</h2>
                <p className="txt_precio">Nuestros precios son por el tiempo que dure su evento y no por un número determinado de horas.</p>
            </div>
        </div>
    );
};

const Nosotros = styled(NosotrosBase)`
    margin-top: 5rem;
    height: 6rem;      
    position: relative;
    ${fixed( )};


    .nosotros{
        font-size: 3rem;
        text-align:center;
        font-weight: 700;
        ${setFlex()};
        letter-spacing: .4rem;
        color: ${colors.white_1};  
        text-shadow: 4px 4px 15px ${colors.blue_6};
    }

    .txt_nosotros{
        font-size: 1.2rem;
        letter-spacing: .2rem;
        text-align:center;
        line-height: 2.5rem;
        color: ${colors.white_1};
        margin: auto 12rem;
    }

    .txt_dos_nosotros{
        font-size: 1.2rem;
        letter-spacing: .2rem;
        text-align:center;
        line-height: 2.5rem;
        color: ${colors.white_1};
        margin: 2rem 12rem;
    }


    .modelos_dif{
        width: 33%;
        margin: 2rem 0rem 0rem 1rem;
        display: inline-block;
        text-align: center;

        .many_icon{
            display: block;
            margin: 0 auto;
        }


        .title_modelos_dif{
            color: ${colors.white_1};
            font-size: 1.2rem;
            letter-spacing: .1rem;
            font-weight: 500;
        }

        .txt_mod_diferentes{
            color: ${colors.white_1};
            font-size: .9rem;
            letter-spacing: .1rem;
            margin: auto 6rem;
            line-height: 1.4rem;
        }

    }

    .eventos{
        width: 32%;
        margin: 2rem 0rem 0rem 0rem;
        display: inline-block;
        text-align: center;

        .event_icon{
            display: block;
            margin: 0 auto;
        }

        .title_eventos{
            color: ${colors.white_1};
            font-size: 1.2rem;
            letter-spacing: .1rem;
            font-weight: 500;
        }

        .txt_eventos{
            color: ${colors.white_1};
            font-size: .9rem;
            letter-spacing: .1rem;
            margin: auto 6rem;
            line-height: 1.4rem;
        }

    }

    .precio{
        width: 33%;
        margin: 2rem 0rem 0rem 0rem;
        display: inline-block;
        text-align: center;

        .precio_icon{
            display: block;
            margin: 0 auto;
        }

        .title_precio{
            color: ${colors.white_1};
            font-size: 1.2rem;
            letter-spacing: .1rem;
            font-weight: 500;
        }

        .txt_precio{
            color: ${colors.white_1};
            font-size: .9rem;
            letter-spacing: .1rem;
            margin: auto 6rem;
            line-height: 1.4rem;
        }

    }

`;


export default Nosotros;
