import React, { useState } from "react";
import styled from 'styled-components';
import {setFlex, colors } from '../utilities';
import {listItems} from '../actions/itemActions';
import {useSelector, useDispatch} from 'react-redux';

function SearchBarBase({ className, category}) {

    // state to track search keyword
    const [searchKeyword, setSearchKeyword] = useState('');
    const [sizeOrder, setSizeOrder] = useState('');
    const [priceOrder, setPriceOrder] = useState('');

    const itemList = useSelector( state => state.items );
    const {items} = itemList;
    const dispatch = useDispatch();

    const handleSubmit = (e) => {        
        // trigger filter dispatch 
        setSearchKeyword(e.target.value)
        console.log(searchKeyword)     
    }

    const sizeHandler = (e) => {
      const size = e.target.value
      // trigger sort dispatch
      const url =  
      dispatch(listItems(category, size, 'gte', 500, '-precio'))      
    }

    const priceHandler = (e) => {
        const price = e.target.value
        // trigger sort dispatch
        console.log(price)
      }

    return (
    <div className={className}>
        <div className="filter_box">
            <form onSubmit={handleSubmit}>                             
                <input                
                    type="text"
                    placeholder="Busca un producto..."
                    name="searchKeyword"
                    value={searchKeyword}
                    onChange={handleSubmit}
                />       
            </form>
            <select name="sizeOrder" onChange={sizeHandler}>
                <option value="">Tamaño</option>
                <option value="chico">Chico</option>
                <option value="mediano">Mediano</option>
                <option value="grande">Grande</option>
                <option value="adulto">Adultos</option>            
            </select>
            <select name="sortOrder" onChange={priceHandler}>
                <option value="">Precio</option>
                <option value="-precio">precio +</option>
                <option value="precio">precio -</option>
                <option value="precio[lte]=1000"> - $1,000 </option>
                <option value="precio[gte]=1000"> + $1,000</option>
                <option value="precio[gte]=2000"> + $2,000</option>            
            </select>           
        </div>
    </div>
    );
}

const SearchBar = styled(SearchBarBase)`
    position: relative;
    width: 90%;
    height: 3rem;
    margin: 0 auto;    

    .filter_box{      
      ${setFlex({x:"space-around", y:"flex-start"})};
      flex-flow: row nowrap;
      border-radius: .8rem;
      padding:0;
    }
`;


export default SearchBar;

