import React from 'react';
import styled from 'styled-components';
import { NavLink } from 'react-router-dom';
import { colors, demotion, elevation, setFlex, fixed } from '../utilities';
import Bill from '../elements/svg/Bill';
import Lighting from '../elements/svg/Lighting';
import Space from '../elements/svg/Space';
import Delivery from '../elements/svg/Delivery';


const CondicionesRentaBase = ({className}) => {
    return (
        <div className={className}>
            <div className="price">
                <Bill className="rent_icon" size="55"/>
                <div className="list">
                    <li className="txt-cond-price">Por evento diario con horario máximo para recoger el mismo día a las 21:30 hrs.</li>
                    <li className="txt-cond-price"> Más IVA en caso de requerir factura.</li>
                </div>
            </div>
            <div className="light">
                <Lighting className="rent_icon" size="55"/>
                <div className="list">
                    <li className="txt-cond-light">Contacto de energía eléctrica a una distancia máxima de 20 mts.</li>
                    <li className="txt-cond-light">No nos conectamos a cajas de fusibles, medidores, diablitos, etc.</li>
                    <li className="txt-cond-light"> Tener buen suministro de electricidad para que los motores funcionen correctamente.</li>
                </div>
            </div>
            <div className="space">
                <Space className="rent_icon" size="55"/>
                <div className="list">
                    <li className="txt-cond-space">Contar con espacio suficiente para instalar el juego inflable de acuerdo a las medidas indicadas para cada modelo.</li>
                    <li className="txt-cond-space">Se debe considerar una holgura de al menos 50 cms de cada lado y 30 cms de altura.</li>
                    <li className="txt-cond-space">De lo contrario no podrá proporcionarse el servicio y se cobrará el 50% del mismo por los costos generados.</li>
                </div>
            </div>
            <div className="cancellations">
                <Delivery className="rent_icon" size="55"/>
                <div className="list">
                    <li className="txt-cond-cancellations">Los precios pueden variar dependiendo de la distancia del evento.</li>
                    <li className="txt-cond-cancellations">Los precios pueden variar en caso de que sea necesario subir más de 10 escalones.</li>
                </div>
            </div>


        </div>
    );
};

const CondicionesRenta = styled(CondicionesRentaBase)`
    position: relative;
    width: 100%;
    margin-top: 8rem;


    .rent_icon{
        display: inline-block;      
    }

    .list{
        display: inline-block;
        margin-left: 1rem;
        list-style-type: square;
    }


    .price{
        width: 95%;
        height: 5rem;
        margin: 0.5rem 0 2.5rem 3rem;
    }

    .light{
        width: 95%;
        height: 5rem; 
        margin: 0.5rem 0 2.5rem 3rem;
    }

    .space{
        width: 95%;
        height: 5rem;        
        margin: 0.5rem 0 2.5rem 3rem;
    }

    .cancellations{
        width: 95%;
        height: 5rem;      
        margin: 0.5rem 0 2.5rem 3rem;
    }


    .txt-cond-price{
        color: ${colors.white_1};
        font-size: .9rem;
        margin: 0 0 0 5rem;
        line-height: 1.1rem;
        padding-bottom: 0.7rem;
    }

    .txt-cond-light{
        color: ${colors.white_1};
        font-size: .9rem;
        margin: 0 0 0 5rem;
        line-height: 1.1rem;
        padding-bottom: 0.7rem;
    }

    .txt-cond-space{
        color: ${colors.white_1};
        font-size: .9rem;
        margin: 0 0 0 5rem;
        line-height: 1.1rem;
        padding-bottom: 0.7rem;        
    }

    .txt-cond-cancellations{
        color: ${colors.white_1};
        font-size: .9rem;
        margin: 0 0 0 5rem;
        line-height: 1.1rem;
        padding-bottom: 0.7rem;
    }


`



export default CondicionesRenta;
