import React, {useState, useEffect} from 'react';
import useCustomFetch from '../../hooks/useCustomFetch';
import ItemListCard from '../../layout/ItemListCard';
import {ItemListContainer} from '../../elements/Container';
import itemDataService from '../../config/itemService';

const ItemList = ({match }) => {

    const [items, setItems] = useState([]);
    const [url, setUrl] = useState(null);
    const [results, err, loading] = useCustomFetch(url);

    const category = match.params.category;

    useEffect( () => {
        retrieveItems();
    }, [url, category]);

    /*
    useEffect( () => {
        setUrl(`/items/${category}`)
    }, [url, category]);

    useEffect( () => {
        setItems(results)
    }, [results]);
    */


    const retrieveItems = () => {
        itemDataService.getAllCategoryItems()
          .then(response => {
            setItems(response.data);
            console.log(response.data);
          })
          .catch(e => {
            console.log(e);
          });
      };

    return (
        <>          
          <ItemListContainer>
            <h3>{category}</h3>
            {items && items.map( (item,idx) =>              
                <ItemListCard                     
                    key={idx}
                    id={item.id}
                    nombre={item.nombre}
                    tamaño={item.tamaño}
                    precio={item.precio}                    
                    paquete={item.paquete}
                    category={match.params.category}
                    fotoCover={item.fotoCover}
                />
            )}            
          </ItemListContainer>
        </>
    );
};

export default ItemList;
