import {ITEM_LIST_REQUEST, ITEM_LIST_SUCESS, ITEM_LIST_FAIL, ITEM_DETAIL_REQUEST, ITEM_DETAIL_SUCCESS, ITEM_DETAIL_FAIL} from './types';
import service from '../config/axios_service';

const listItems =  (category, size = '', price_cond = '', amt = '', sortby = '') => async (dispatch) => {
  try {
    dispatch({ type: ITEM_LIST_REQUEST });
    const response = await service.get(`/items/${category}?size=${size}&adultos=${true}&precio[${price_cond}]=${amt}&sort=${sortby}`);
    console.log(response)
    dispatch({ type: ITEM_LIST_SUCESS, payload: response.data.data.items });
  } catch (error) {
    dispatch({type: ITEM_LIST_FAIL, payload: error.message});
  }
}

const detailItem =  (category, id) => async (dispatch) => {
  try {
    dispatch({ type: ITEM_DETAIL_REQUEST});
    const response = await service.get(`/items/${category}/${id}`);    
    dispatch({ type: ITEM_DETAIL_SUCCESS, payload: response.data.data.item });
    console.log(response.data.data.item)
  } catch (error) {
    dispatch({type: ITEM_DETAIL_FAIL, payload: error.message});
  }
}


export {listItems, detailItem};

