import React from 'react'
import {Link} from 'react-router-dom';
import styled from 'styled-components';
import {colors, setFlex} from '../utilities';

function Card({className, nombre, desc, observaciones, precio, medidas, fotoCover, fotos, back}) {
    return (                		    
      <div className={className} >        
        {/* ---  DESCRIPTION --- */}
        <div className="dtl__box">  
        <h2 onClick={back}>{nombre}</h2>          
          <div className="description">
            <h4>Descripción</h4>
            <div className="desc_txt">{desc}</div>
          </div>
          <h4>Medidas</h4>
          <div className="numbers">          
            <div className="medidas">              
              <p><span className="ms">Ancho:</span> {medidas && medidas[0]}m.</p>
              <p><span className="ms">Largo:</span> {medidas && medidas[1]}m.</p>
              <p><span className="ms">Alto:</span> {medidas && medidas[2]}m.</p>
            </div>                       
            <div className="precio">
              <p>Costo por día: ${precio}</p>
            </div>
          </div>          
          <p className="observaciones">{observaciones}</p>
        </div>    
        <Link to='/contacto' className="round-black-btn">Reservar</Link>

         {/* ---  PICTURES --- */}
         <div className="product-pics">
          <div className="foto_cover">
            <img src={fotoCover} alt={nombre} className="card__img"/>
          </div>
          <div className="fotos">
          {fotos && fotos.map( (foto, idx) => 
            <img  key={idx}  src={foto}  alt="" className="card__img"/>
          )}
          </div>
        </div>


      </div>        


    )
}

const ItemDetailCard = styled(Card)`
  width: 100%;
  margin: 1rem auto;
  padding: 1.5rem;
  color: ${colors.white_1};
  background-color: blue;  
  ${setFlex({ x:"space-between", y:'flex-start' })};

  .dtl__box{    
    background-color: red;
    width: 50%;

    h2{
      width: 100%;
      text-align:center;
      text-transform: uppercase;
      font-size: 1.5rem;
    }

    h4{
      font-size: 1rem;
    }

    .description{                        
      background-color: purple;      

      .desc_txt{
        line-height: 1.1rem;
        text-align: justify;
      }
    }

    .numbers{
      background-color: green;
      font-size: .8rem;
      ${setFlex({ x:"space-between"})};      

      .medidas{
        background-color: yellowgreen;
        flex-flow: column;
        width: 30%;
        margin-left: 2rem;

        p{padding: 0rem;}

        .ms{
          font-weight: bold;
        }
      }

      .precio{
        width: 35%;      
        margin-right: 0rem;
        background-color: gray;

        p {          
          font-weight: bolder;
          letter-spacing: .1rem;
        }
      }
    }    

    .observaciones{        
      background-color: black;
      line-height: 1.1rem;       
    }

  } /* finish dtl_box */

  .product-pics{    
    background-color: orange;   
    width: 50%;
    height: 35vh;    

    img{
      height: 4rem;
    }
  } /* finish product-pics */
`;

export default ItemDetailCard
