import {ITEM_LIST_REUQEST, ITEM_LIST_SUCESS, ITEM_LIST_FAIL} from './types';
import service from '../config/axios_service';

const listItems =  (category) => async (dispatch) => {
  try {
    dispatch({ type: ITEM_LIST_REUQEST });
    const response = await service.get(`/items/${category}`);
    console.log(response)
    dispatch({ type: ITEM_LIST_SUCESS, payload: response.data.data.items });
  } catch (error) {
    dispatch({type: ITEM_LIST_FAIL, payload: error.message});
  }
}


export {listItems};

