import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, below, colors, absolute, above} from '../../utilities';
import {SliderContainer} from '../Container';
import Castle from '../svg/Castle.jsx';
import {SliderLink} from '../Button';

const Inflables = ({className}) => {
  return (
    <SliderContainer className={className}>    
      <div className="icon"><Castle size={40}/></div>
      <h3 className="title slide_title">Juegos Inflables</h3>
      <img className="img" src="https://partyplace.mx/wp-content/uploads/2019/06/2-2.png" alt=""/>
      <div className="text">
        <p className="txt">MÃ¡s de 60 modelos diferentes</p>
        <p className="txt">Desde $600 por evento</p>
      </div> 
      <div className="link">
        <SliderLink>
          <Link to={'/items/inflable'}>Expolorar</Link> 
        </SliderLink>
      </div>      
    </SliderContainer>
  );
}

const SlideInflables = styled(Inflables)`  
  background-image: linear-gradient(to right, ${colors.blue_3}, ${colors.noryan_blue});
  position: relative;
  ${setFlex({x:"space-around"})};

  background:
  linear-gradient(135deg, rgb(119,188,239, 0.5) 25%, transparent 25%) -50px 0,
  linear-gradient(225deg, rgb(119,188,239, 0.5) 25%, transparent 25%) -50px 0,
  linear-gradient(315deg, rgb(119,188,239, 0.5) 25%, transparent 25%),
  linear-gradient(45deg, rgb(119,188,239, 0.5) 25%, transparent 25%);
  background-size: 100px 100px;
  background-color: rgb(94,138,253, 0.5);

  .title{
    ${absolute({ x:"10%", y:"1.5rem" })};
  }

  img{     
     height: 60%;
     ${absolute({ x:"5%", y:"20%" })};
  }

  .text{
    ${absolute({ x:"40%", y:"15%" })};
    line-height: 3rem;

    .txt{
      margin-top: 1rem;
    }
  }

  .link{
    ${absolute({ xP:"right", x:"10%", yP:"bottom", y:"30%" })};
  }

  ${below.md`
    .title{
      ${absolute({ x:"25%", y:"2rem" })};
      width: 100%;
      font-size: 2rem;
    }
    .text{
      ${absolute({ x:"0%", y:"18%" })};
      line-height: 2rem;

    .txt{
      margin-top: .5rem;
    } 
    }
    img{
     height: 30%;
     ${absolute({ x:"20%", y:"60%" })};
    }
    .link{
      position: static;
      margin: 0 auto;
      margin-top: 3rem;
    }
  `}

  ${below.sm`
    background-color: ${colors.red_2};
  `}

`;

export default SlideInflables;
