import React from 'react';
import styled from 'styled-components';
//import {SiliderButton} from '../elements/Button';
import {absolute} from '../utilities';

const SliderBase = ({className}) => {

  let sliderComponents = [1,2,3,4];

  return (

    <div className={className}>
      {sliderComponents.map( (component,idx) => {
            return (
              <div key={idx} className="slide">
                {component}
              </div>
            )
      })}

      <button className="slide_btn_left">left</button>
      <button className="slide_btn_right">right</button>
    </div>
  );
}

const Slider = styled(SliderBase)`
  background-color: red;
  width: 100%;
  height: 90vh;
  margin: 0;
  padding: 0;
  display: flex;
  align-items: center;  

  .slide{ 
    background-color: blue;
    min-width: 100%;
    height: 80%;
    position: relative; 
  }

  .slide_btn_left{
    background-color: yellowgreen;
    ${absolute({ y:"55%" })};
    transform: translateY(-50%);
    width: 5%;
    height: 70%;
  }

  .slide_btn_right{
    background-color: yellowgreen;
    ${absolute({ y:"55%", xP:"right", x:".5rem" })};
    transform: translateY(-50%);
    width: 5%;
    height: 70%;
  }

`;


export default Slider;
