import React, { useState } from "react";
import styled from 'styled-components';
import {setFlex } from '../utilities';
import {listItems} from '../actions/itemActions';
import {useDispatch} from 'react-redux';

function SearchBarBase({ className, category}) {

    // state to track search keyword
    const [searchKeyword, setSearchKeyword] = useState('');
    const [size, setSize] = useState('');
    const [priceOrder, setPriceOrder] = useState('');
    const [price, setPrice] = useState('');

    //;const itemList = useSelector( state => state.items );
    //const {items} = itemList;
    const dispatch = useDispatch();

    const handleSubmit = (e) => {        
        // trigger filter dispatch 
        setSearchKeyword(e.target.value)
        console.log(searchKeyword)     
    }

    const sizeHandler = (e) => {      
      setSize(e.target.value)
      // trigger sort dispatch 
      dispatch(listItems(category, size, null , null))
      console.log(size)   
    }

    const priceHandler = (e) => {        
        setPrice(e.target.value)
        // trigger sort dispatch           
        console.log(price)
        /*
        if(price === "-precio" || price === "precio"){
            setPrice(price)            
            dispatch(listItems(category, size, null, price))            
        }
        if(price === "precio[lte]=1500" || "precio[lte]=3000" || "precio[lte]=4500"){
            setPriceOrder(price)
            dispatch(listItems(category, size, priceOrder, null))
        }      
        */

      }


    return (
    <div className={className}>
        <div className="filter_box">
            <form onSubmit={handleSubmit}>                             
                <input                
                    type="text"
                    placeholder="Busca un producto..."
                    name="searchKeyword"
                    value={searchKeyword}
                    onChange={(e) => setSearchKeyword(e.target.value)}
                />       
            </form>
            <select name="size" onChange={sizeHandler}>
                <option value="">Tamaño</option>
                <option value="chico">Chico</option>
                <option value="mediano">Mediano</option>
                <option value="grande">Grande</option>                
            </select>
            <select name="price" onChange={priceHandler}>
                <option value="">Precio</option>
                <option value="-precio">Mayor</option>
                <option value="precio">Menor</option>
                <option value="precio[lte]=1000"> - $1,000 </option>
                <option value="precio[gte]=1500"> + $1,500</option>
                <option value="precio[gte]=2000"> + $2,000</option>            
            </select>           
        </div>
    </div>
    );
}

const SearchBar = styled(SearchBarBase)`
    position: relative;
    width: 90%;
    height: 3rem;
    margin: 0 auto;    

    .filter_box{      
      ${setFlex({x:"space-around", y:"flex-start"})};
      flex-flow: row nowrap;
      border-radius: .8rem;
      padding:0;
    }
`;


export default SearchBar;

