import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, elevation, colors } from '../../utilities';
import {SliderContainer} from '../Container';
import Fut from '../svg/Fut.jsx';
import {SliderLink} from '../Button';

const Futbolito = ({className}) => {
  return (
    <SliderContainer className={className}>    
    <div className="icon"><Fut size={40}/></div>    
      <h3 className="title slide_title">Futbolitos</h3>
      <div className="text">
      <p className="txt">Infantil y adultos</p>
      </div>      
      <SliderLink>
        <Link to={'/items/futbolito'}>Explorar</Link> 
      </SliderLink>
    </SliderContainer>
  );
}

const SlideFut = styled(Futbolito)`
  background-image: linear-gradient(to right, ${colors.green_blue}, ${colors.blue_5});

  background-color:white;
  background-image: linear-gradient(90deg, rgb(94,138,253, 0.6) 50%, transparent 50%),
  linear-gradient(rgb(119,188,239, 0.7) 50%, transparent 50%);
  background-size:50px 50px;



`;

export default SlideFut;
