import React, {useState} from 'react';
import styled from 'styled-components';
import {setFlex, colors, below, elevation} from '../utilities';

function Filter({className}) {

  const [checked, setChecked] = useState('');

  function handleChange(e){
    if(e.target.checked){ 
      setChecked(e.target.value);      
    }
  }

  const nextAddressStep = (e) => {   
    e.preventDefault();    
    console.log('sameAddress question response saved ...')
    if(checked === 'NO'){

    }
    if(checked === 'YES'){

    }        
  }

  return (
    <div className={className}>      
      <form onSubmit={nextAddressStep}>      
          <div className="radio">            
                <input                   
                  type="radio"                                    
                  value="Chicos"                  
                  checked={checked === "Chicos"}
                  onChange={handleChange}/>
                <label htmlFor="radio-1" className="radio-label"><span>Chicos</span></label>                        
                <input                   
                  type="radio"                                                      
                  value="Medianos"
                  checked={checked === "Medianos"}
                  onChange={handleChange}/>                  
                <label  htmlFor="radio-2" className="radio-label"><span>Medianos</span></label>                        
                <input                   
                  type="radio"                                                      
                  value="Grandes"
                  checked={checked === "Grandes"}
                  onChange={handleChange}/>                  
                <label  htmlFor="radio-2" className="radio-label"><span>Grandes</span></label>                        
                <input                   
                  type="radio"                                                      
                  value="Precio"
                  checked={checked === "Precio"}
                  onChange={handleChange}/>                  
                <label  htmlFor="radio-2" className="radio-label"><span>Precio</span></label>            
          </div>          
      </form>
    </div>
  );
}

const ItemFilter = styled(Filter)`
    position: relative;
    width: 100%;    
    margin: 0 auto;

    .radio{
      height: 2.5rem;
      ${elevation[3]};
      border-radius: .3rem;
      width: 100%;
      margin: 0 auto;    
      ${setFlex({ x:"space-around"})};
    }

    .radio input[type="radio"] {      
      opacity: 1;
      z-index: 999;      
    }

    .radio input[type="radio"] + .radio-label::before {
      content: '';      
      border-radius: .5rem;
      border: none;
      display: inline-block;
      width: 4rem;
      height: 3em;   
      vertical-align:top;
      cursor: pointer;      
      z-index: 1; 
    }

    .radio input[type="radio"]:checked + .radio-label::before {

      font-size: 1rem;
      font-weight: bold;
      z-index: 2;
    }

    .radio input[type="radio"] + .radio-label:empty::before {
      margin-right: 0;
      z-index: 3;
    }

    .radio-label{
      position: relative;

      span{      
        color: ${colors.white_1};
        position: absolute;
        top: 30%;
        left: 10%;
        z-index: 4;
        font-size: .8rem;
      }
    }


    ${below.md`                      

  `}
`;

export default ItemFilter;
