import React, { useState, useEffect } from "react";
import styled from 'styled-components';
// import SearchItemList from '../components/items/SearchItemList';
import {setFlex, fixed, colors, elevation} from '../utilities';
import { SearchIcon } from '../elements/Icons';


function SearchBarBase({ className }) {

    const [search, setSearch] = useState(null);

    function handleSearch (e) {
            setSearch(e.target.value);
        }

        return (
        <div className={className}>
            <form>
                <label className="search_label">
                    Buscar un producto...
                    <input
                        className="text_input"
                        type="text"
                        value={search}
                        // onChange={handleSearch}
                    />
                    <SearchIcon className="icon_search" onClick={handleSearch} size="25"/>
                </label>
            </form>
        </div>
    );
}

const SearchBar = styled(SearchBarBase)`
    position: relative;
    width: 100%;    
    height: 3rem;      
    background-color: ${colors.blue_2};  
    margin-top: 6rem;
    ${fixed( )};
    ${setFlex( )};

    .search_label{
        width: 100%;
        padding: 0.4rem;
        color: ${colors.txt_1};
        font-size: 0.9rem;
        background-color: ${colors.white_1};
        border-radius: 1rem;
    }

    .text_input{
        border: none;
        width: 14rem;
    }

    .icon_search{
        cursor: pointer;

    }
`;


export default SearchBar;
