import React, {useState, useEffect} from 'react';
//import useCustomFetch from '../../hooks/useCustomFetch';
import {useSelector, useDispatch} from 'react-redux';
import {getItemDetail} from '../../actions/itemActions.js';
import {ItemDetailContainer} from '../../elements/Container';
import ItemDetailCard from '../../layout/ItemDetailCard';

function ItemDetail({match}) {

    /*
        const [item, setItem] = useState({});
    const [url, setUrl] = useState(null);
    const [results, result, err, loading] = useCustomFetch(url);

    useEffect( () => {
        setUrl(`/items/${match.params.category}/${match.params.id}`)
    }, [url, match.params.category, match.params.id]);

    useEffect( () => {
        setItem(result)        
    }, [result]);

    const {medidas} = item;
    */

    const itemDetail = useSelector(state => state.item);
    const {item, loading} = itemDetail;
    const dispatch = useDispatch();

    useEffect( () => {
        dispatch(getItemDetail(match.params.category, match.params.id))
    }, [dispatch, match.params.category, match.params.id]);

    return (
        <ItemDetailContainer>
            {loading && <div>LOADING</div>}
           {item &&  <ItemDetailCard
                nombre={item.nombre}
                precio={item.precio}
                desc={item.descripcion}
                observaciones={item.observaciones}                
                medidas={item.medidas}
                fotoCover={item.fotoCover}
            />}
        </ItemDetailContainer>
    )
}

export default ItemDetail;
