import React, {useState} from 'react';
import SOne from '../elements/slider/SlideOne';
import STwo from '../elements/slider/SlideTwo';
import SThree from '../elements/slider/SlideThree';
import SFour from '../elements/slider/SlideFour';
import styled from 'styled-components';
import Next from '../elements/svg/Next.jsx';
import Prev from '../elements/svg/Prev.jsx';
import {absolute, colors, demotion} from '../utilities';

const SliderBase = ({className}) => {

  const [x, setX] = useState(0);

  let sliderComponents = [<SOne/>,<STwo/>,<SThree/>,<SFour/>]; 

  const goLeft = () => {
    setX(x + 100);
    if(x === 0 ? setX( -100 * (sliderComponents.length -1 ) ) : setX(x + 100) );
  }

  const goRight = () => {    
    if(x === -100 * (sliderComponents.length -1 ) ? setX(0) : setX(x - 100) );
  }

  return (

    <div className={className}>
      {sliderComponents.map( (component,idx) => {
            return (
              <div key={idx} className="slide" style={{ transform:`translateX(${x}%)` }}>
                {component}
              </div>
            )
      })}

      <button className="slide_btn_left" onClick={goLeft}><Prev/></button>
      <button className="slide_btn_right" onClick={goRight}><Next/></button>
    </div>
  );
}

const Slider = styled(SliderBase)`  
  width: 100%;
  height: 80vh;
  box-sizing: border-box;
  margin: 0;
  padding: 0;
  display: flex;
  align-items: center;  

  .slide{ 
    ${demotion[3]};
    min-width: 100%;
    height: 90%;
    position: relative;
    transition: .5s;
    border-radius: .8rem;  
  }

  .slide_btn_left{
    background-color: transparent;
    ${absolute({ y:"50%" })};
    transform: translateY(-50%);
    width: 5%;
    height: 70%;
    cursor: pointer;
    border: none;
    outline: 0;
    border-radius: .8rem;

    &:hover{
      background-color: ${colors.blue_1};
      opacity: .3;
    }

    &:active{
      border: none;
    }

  }

  .slide_btn_right{
    background-color: transparent;
    ${absolute({ y:"50%", xP:"right", x:".5rem" })};
    transform: translateY(-50%);
    width: 5%;
    height: 70%;
    cursor: pointer;
    border: none;
    outline: 0;
    border-radius: .8rem;

    &:hover{
      background-color: ${colors.blue_1};
      opacity: .3;
    }

    &:active{
      border: none;
    }
  }

`;


export default Slider;
