import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, colors, absolute} from '../../utilities';
import {SliderContainer} from '../Container';
import Micro from '../svg/Micro.jsx';
import {SliderLink} from '../Button';


const Rockolas = ({className}) => {
  return (
<SliderContainer className={className}>        
      <div className="icon"><Micro size={40}/></div>      
      <h3 className="title">Rockolas</h3>

      <div className="img_box">
        <img className="img" src="https://i.postimg.cc/QCfXwHWQ/rocola.png" alt=""/>
      </div>

      <div className="text_box">
        <p className="txt">Pantalla touch, iluminación  más de 150 mil canciones y videos</p>    
      </div>            
      <SliderLink>
        <Link to={'/items/rockola'}>Explorar</Link> 
      </SliderLink>
</SliderContainer>
  );
}

const SlideRock = styled(Rockolas)`
  background-image: linear-gradient(to right, ${colors.blue_3}, ${colors.noryan_blue} );
  position: relative;

  .title{
    width: auto;
    margin: 1rem auto;
    padding-top: 1.5rem;
    padding-left: 10%;    
  }

  .img_box{
    height: 70%;
    ${setFlex({x:"flex-start"})};
    background-color: red; 

    img{
      height: 100%;
    }
  }

  .text_box{
    width: 50%;

    .txt{
      margin-bottom: 1rem;
    }
  }

`;

export default SlideRock;
