import React from 'react';
import { NavLink } from 'react-router-dom';
import styled from 'styled-components';
import {setFlex, colors} from '../utilities';

function NavBarBase({ className }) {
    return (
        <div className={className}>
            <input className="menu-btn" type="checkbox" id="menu-btn"/>
            <label className="menu-icon"></label><span className="navicon"></span>
            <ul className="nav_list menu">
                <li className="nav_item"> <NavLink activeClassName="active_link" className="nav_link" exact to="/items">Catálogo de renta</NavLink></li>
                <li className="nav_item"> <NavLink activeClassName="active_link" className="nav_link" exact to="/paquetes">Renta de paquetes</NavLink></li>
